domain.map:
function(doc) {
  if (doc.records) {
    for (var i = 0; i < doc.records.length; i++) {
      var r = doc.records[i];

      emit(doc.domain, {
        ty: r.type,
        data: r.data,
        ttl: r.ttl || doc.configuration.ttl,
        class: r.class || doc.configuration.class
      })
    }
  }
}
